import React from 'react';
import { useHistory } from 'react-router';
const ProfileSidebar = () =>{
    const[currentTab,setCurrentTab] = React.useState('profile');
    const history = useHistory();
    const handleRequest = (path)=>{
        setCurrentTab(path);
        history.push("/"+path);
    }
    const handleLogout = ()=>{
        localStorage.remove
    }
return (
    <>
<div className="col-md-3 ">
		<div className="list-group ">
            <a href="/profile" className={currentTab === "profile" ? "list-group-item list-group-item-action active": "list-group-item list-group-item-action"}>  Profile</a>
            <a href="/change-password" className={currentTab === "change-password" ? "list-group-item list-group-item-action active": "list-group-item list-group-item-action"}>Change Password</a>
            <a href="/manage-blogs" className={currentTab === "manage-blogs" ? "list-group-item list-group-item-action active": "list-group-item list-group-item-action"}>Blogs</a>
            <a href="/logout" className="list-group-item list-group-item-action" onClick={handleLogout}>Logout</a>
        </div> 
		</div>
    </>
)

}
export default  ProfileSidebar;
